import { Card, Grid, Image, Icon, Modal, Button, Header } from 'semantic-ui-react'
import './App.css';
import useSWR from 'swr'
import React from 'react'
function App() {
  const fetcher = (...args) => fetch(...args).then(res => res.json())
  const { data, error } = useSWR('https://rickandmortyapi.com/api/character', fetcher)
  const [open, setOpen] = React.useState(false)


  const renderCard = (character) => {
    return (<Modal
      onClose={() => setOpen(false)}
      onOpen={() => setOpen(true)}
      open={open}
      trigger={<Card className="card" link >
        <Image src={character.image} wrapped ui={false} />
        <Card.Content>
          <Card.Header>{character.name}</Card.Header>
          <Card.Meta>
            <div>Status: {character.status}</div>
            <div>Species: {character.species}</div>
            <div>Gender: {character.gender}</div>
            <div>Origin: {character.origin.name}</div>
            <div>location: {character.location.name}</div>
          </Card.Meta>

          {/* <Card.Description>
      {character.episode.map((episode) => <Episode episode={episode}></Episode>)}
    </Card.Description> */}
        </Card.Content>
        <Card.Content extra>
          <Icon name='info' />{character.episode.length} Episodes
        </Card.Content>
      </Card>
     }
    >
      <Modal.Header>Select a Photo</Modal.Header>
      <Modal.Content image>
        <Image size='medium' src='https://react.semantic-ui.com/images/avatar/large/rachel.png' wrapped />
        <Modal.Description>
          <Header>Default Profile Image</Header>
          <p>
            We've found the following gravatar image associated with your e-mail
            address.
          </p>
          <p>Is it okay to use this photo?</p>
        </Modal.Description>
      </Modal.Content>
      <Modal.Actions>
        <Button color='black' onClick={() => setOpen(false)}>
          Nope
        </Button>
        <Button
          content="Yep, that's me"
          labelPosition='right'
          icon='checkmark'
          onClick={() => setOpen(false)}
          positive
        />
      </Modal.Actions>
    </Modal>)
  }
  if (data && Object.keys(data).length) {
    return (
      <div className="App">
        <div className="navbar"> RICK AND MORTY</div>
        <Grid className="cardWrapper">
          {data.results.map((character) => renderCard(character))}
        </Grid>
      </div>
    );
  }
  else return <div>Loading..</div>
}

function Episode(episode) {
  const fetcher = (...args) => fetch(...args).then(res => res.json())
  const { data, error } = useSWR(episode.episode || "", fetcher)
  if (data && Object.keys(data).length)
    return <div>{data.name}</div>
  return <div></div>
}

export default App;
